import React, { useState, useRef } from 'react';
import './App.css';

const Node = ({ id, label, position, onDragStart, onClick, isSelected }) => {
  return (
    <div
      className={`node ${isSelected ? 'selected' : ''}`}
      style={{
        left: `${position.x}px`,
        top: `${position.y}px`,
        position: 'absolute',
      }}
      onMouseDown={(e) => onDragStart(e, id)}
      onClick={() => onClick(id)}
    >
      {label}
    </div>
  );
};

const Edge = ({ start, end }) => {
  const calculateLine = (start, end) => {
    const x1 = start.x;
    const y1 = start.y;
    const x2 = end.x;
    const y2 = end.y;
    const dx = x2 - x1;
    const dy = y2 - y1;
    const length = Math.sqrt(dx * dx + dy * dy);
    const angle = Math.atan2(dy, dx) * (180 / Math.PI);

    return {
      width: length,
      transform: `rotate(${angle}deg)`,
      left: x1,
      top: y1,
    };
  };

  const lineStyle = calculateLine(start, end);

  return (
    <div
      style={{
        position: 'absolute',
        width: `${lineStyle.width}px`,
        height: '2px',
        backgroundColor: '#000',
        transformOrigin: '0 0',
        transform: lineStyle.transform,
        left: `${lineStyle.left}px`,
        top: `${lineStyle.top}px`,
      }}
    />
  );
};

const App = () => {
  const [nodes, setNodes] = useState([
    { id: 1, label: 'Node 1', position: { x: 100, y: 100 } },
    { id: 2, label: 'Node 2', position: { x: 300, y: 100 } },
  ]);
  const [edges, setEdges] = useState([]);
  const [draggingNode, setDraggingNode] = useState(null);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [addingEdge, setAddingEdge] = useState(false);
  const [selectedNodes, setSelectedNodes] = useState([]);

  const handleNodeDragStart = (e, id) => {
    setDraggingNode(id);
    const node = nodes.find((node) => node.id === id);
    if (node) {
      setDragOffset({
        x: e.clientX - node.position.x,
        y: e.clientY - node.position.y,
      });
    }
  };

  const handleNodeDragMove = (e) => {
    if (draggingNode !== null) {
      const newX = e.clientX - dragOffset.x;
      const newY = e.clientY - dragOffset.y;
      setNodes((prevNodes) =>
        prevNodes.map((node) =>
          node.id === draggingNode ? { ...node, position: { x: newX, y: newY } } : node
        )
      );
    }
  };

  const handleNodeDragEnd = () => {
    setDraggingNode(null);
  };

  const addNode = () => {
    const newNode = {
      id: nodes.length + 1,
      label: `Node ${nodes.length + 1}`,
      position: { x: Math.random() * 500, y: Math.random() * 500 },
    };
    setNodes([...nodes, newNode]);
  };

  const handleAddEdgeClick = () => {
    setAddingEdge(true);
    setSelectedNodes([]);
  };

  const handleNodeClick = (id) => {
    if (addingEdge) {
      if (selectedNodes.length < 2) {
        setSelectedNodes((prevSelected) => [...prevSelected, id]);
      }
      if (selectedNodes.length === 1) {
        const startNode = nodes.find((node) => node.id === selectedNodes[0]);
        const endNode = nodes.find((node) => node.id === id);
        if (startNode && endNode) {
          setEdges((prevEdges) => [
            ...prevEdges,
            { start: startNode.position, end: endNode.position },
          ]);
        }
        setAddingEdge(false); // Exit edge-adding mode
        setSelectedNodes([]);
      }
    }
  };

  return (
    <div className="graph-editor">
      <div className="button-container">
        <button onClick={addNode}>Add Node</button>
        <button onClick={handleAddEdgeClick} className={addingEdge ? 'active' : ''}>
          Add Edge
        </button>
      </div>
      <div
        className="graph-container"
        onMouseMove={handleNodeDragMove}
        onMouseUp={handleNodeDragEnd}
        onMouseLeave={handleNodeDragEnd}
      >
        {edges.map((edge, index) => (
          <Edge key={index} start={edge.start} end={edge.end} />
        ))}
        {nodes.map((node) => (
          <Node
            key={node.id}
            id={node.id}
            label={node.label}
            position={node.position}
            onDragStart={handleNodeDragStart}
            onClick={handleNodeClick}
            isSelected={selectedNodes.includes(node.id)}
          />
        ))}
      </div>
    </div>
  );
};

export default App;
