// src/App.js
import React, { useState } from 'react';
import Node from './components/Node';
import Edge from './components/Edge';
import './App.css';

const App = () => {
  const [nodes, setNodes] = useState([
    { id: 1, label: 'Node 1', position: { x: 100, y: 100 } },
    { id: 2, label: 'Node 2', position: { x: 300, y: 100 } },
  ]);

  const [edges, setEdges] = useState([
    { start: { x: 100, y: 100 }, end: { x: 300, y: 100 } },
  ]);

  const handleNodeDrag = (e, id) => {
    const newPosition = { x: e.clientX - 50, y: e.clientY - 25 };
    setNodes((prevNodes) =>
      prevNodes.map((node) =>
        node.id === id ? { ...node, position: newPosition } : node
      )
    );
  };

  return (
    <div className="graph-editor">
      {edges.map((edge, index) => (
        <Edge key={index} start={edge.start} end={edge.end} />
      ))}
      {nodes.map((node) => (
        <Node
          key={node.id}
          id={node.id}
          label={node.label}
          position={node.position}
          onDrag={handleNodeDrag}
        />
      ))}
    </div>
  );
};

export default App;
