import React, { useState } from 'react';
import './App.css';

const Node = ({ id, label, position, onDrag, onClick, selected }) => {
  return (
    <div
      className={`node ${selected ? 'selected' : ''}`}
      style={{
        left: position.x,
        top: position.y,
        position: 'absolute',
        cursor: 'move',
      }}
      draggable
      onDrag={(e) => onDrag(e, id)}
      onClick={() => onClick(id)}
    >
      {label}
    </div>
  );
};

const Edge = ({ start, end }) => {
  const length = Math.hypot(end.x - start.x, end.y - start.y);
  const angle = Math.atan2(end.y - start.y, end.x - start.x);
  const edgeStyle = {
    position: 'absolute',
    width: length,
    height: 2,
    backgroundColor: 'black',
    transformOrigin: '0 0',
    transform: `rotate(${angle}rad)`,
    left: start.x,
    top: start.y,
  };

  return <div style={edgeStyle}></div>;
};

const App = () => {
  const [nodes, setNodes] = useState([
    { id: 1, label: 'Node 1', position: { x: 100, y: 100 }, selected: false },
    { id: 2, label: 'Node 2', position: { x: 300, y: 100 }, selected: false },
  ]);
  const [edges, setEdges] = useState([]);
  const [addingEdge, setAddingEdge] = useState(false);
  const [selectedNodes, setSelectedNodes] = useState([]);

  const handleNodeDrag = (e, id) => {
    const newPosition = { x: e.clientX - 50, y: e.clientY - 25 };
    setNodes((prevNodes) =>
      prevNodes.map((node) =>
        node.id === id ? { ...node, position: newPosition } : node
      )
    );
  };

  const handleNodeClick = (id) => {
    if (addingEdge) {
      if (selectedNodes.length < 2) {
        const newSelectedNodes = [...selectedNodes, id];
        setSelectedNodes(newSelectedNodes);
        // Change node color when selected
        setNodes((prevNodes) =>
          prevNodes.map((node) =>
            newSelectedNodes.includes(node.id)
              ? { ...node, selected: true }
              : node
          )
        );
        if (newSelectedNodes.length === 2) {
          addEdge(newSelectedNodes[0], newSelectedNodes[1]);
          setAddingEdge(false); // Reset after adding edge
          setSelectedNodes([]);
          resetNodeSelection();
        }
      }
    }
  };

  const addEdge = (startNodeId, endNodeId) => {
    const startNode = nodes.find((node) => node.id === startNodeId);
    const endNode = nodes.find((node) => node.id === endNodeId);

    if (startNode && endNode) {
      // Calculate the edge between nodes based on outer boundary
      setEdges([...edges, { start: startNode.position, end: endNode.position }]);
    }
  };

  const resetNodeSelection = () => {
    setNodes((prevNodes) =>
      prevNodes.map((node) => ({ ...node, selected: false }))
    );
  };

  const handleAddEdgeClick = () => {
    setAddingEdge(true);
    setNodes((prevNodes) =>
      prevNodes.map((node) => ({ ...node, selected: false }))
    );
  };

  return (
    <div className="graph-editor">
      <button
        className={`add-edge-button ${addingEdge ? 'active' : ''}`}
        onClick={handleAddEdgeClick}
      >
        Add Edge
      </button>
      {edges.map((edge, index) => (
        <Edge key={index} start={edge.start} end={edge.end} />
      ))}
      {nodes.map((node) => (
        <Node
          key={node.id}
          id={node.id}
          label={node.label}
          position={node.position}
          onDrag={handleNodeDrag}
          onClick={handleNodeClick}
          selected={node.selected}
        />
      ))}
    </div>
  );
};

export default App;
