import React, { useState } from 'react';
import './App.css';

const Node = ({ id, label, position, onDrag, onClick, selected }) => {
  return (
    <div
      className={`node ${selected ? 'selected' : ''}`}
      style={{
        left: position.x,
        top: position.y,
        position: 'absolute',
        cursor: 'move',
        transform: 'translate(-50%, -50%)',
      }}
      onMouseDown={(e) => onDrag(e, id)}
      onClick={() => onClick(id)}
    >
      {label}
    </div>
  );
};

const Edge = ({ start, end }) => {
  const length = Math.hypot(end.x - start.x, end.y - start.y);
  const angle = Math.atan2(end.y - start.y, end.x - start.x);
  const edgeStyle = {
    position: 'absolute',
    width: length,
    height: 2,
    backgroundColor: 'black',
    transformOrigin: '0 0',
    transform: `rotate(${angle}rad)`,
    left: start.x,
    top: start.y,
  };

  return <div style={edgeStyle}></div>;
};

const App = () => {
  const [nodes, setNodes] = useState([
    { id: 1, label: 'Node 1', position: { x: 100, y: 100 }, selected: false },
    { id: 2, label: 'Node 2', position: { x: 300, y: 100 }, selected: false },
  ]);
  const [edges, setEdges] = useState([]);
  const [addingEdge, setAddingEdge] = useState(false);
  const [selectedNodes, setSelectedNodes] = useState([]);
  const [draggingNode, setDraggingNode] = useState(null);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });

  const handleNodeDragStart = (e, id) => {
    const node = nodes.find((node) => node.id === id);
    setDraggingNode(node);
    setDragOffset({ x: e.clientX - node.position.x, y: e.clientY - node.position.y });
  };

  const handleNodeDrag = (e) => {
    if (draggingNode) {
      const newPosition = {
        x: e.clientX - dragOffset.x,
        y: e.clientY - dragOffset.y,
      };
      setNodes((prevNodes) =>
        prevNodes.map((node) =>
          node.id === draggingNode.id ? { ...node, position: newPosition } : node
        )
      );
    }
  };

  const handleNodeDragEnd = () => {
    setDraggingNode(null);
  };

  const handleNodeClick = (id) => {
    if (addingEdge) {
      if (selectedNodes.length < 2) {
        const newSelectedNodes = [...selectedNodes, id];
        setSelectedNodes(newSelectedNodes);
        // Change node color when selected
        setNodes((prevNodes) =>
          prevNodes.map((node) =>
            newSelectedNodes.includes(node.id)
              ? { ...node, selected: true }
              : node
          )
        );
        if (newSelectedNodes.length === 2) {
          addEdge(newSelectedNodes[0], newSelectedNodes[1]);
          setAddingEdge(false); // Reset after adding edge
          setSelectedNodes([]);
          resetNodeSelection();
        }
      }
    }
  };

  const addEdge = (startNodeId, endNodeId) => {
    const startNode = nodes.find((node) => node.id === startNodeId);
    const endNode = nodes.find((node) => node.id === endNodeId);

    if (startNode && endNode) {
      // Create edge between nodes based on their positions
      setEdges([...edges, { start: startNode.position, end: endNode.position }]);
    }
  };

  const resetNodeSelection = () => {
    setNodes((prevNodes) =>
      prevNodes.map((node) => ({ ...node, selected: false }))
    );
  };

  const handleAddEdgeClick = () => {
    setAddingEdge(true);
    setNodes((prevNodes) =>
      prevNodes.map((node) => ({ ...node, selected: false }))
    );
  };

  const addNode = () => {
    const newNode = {
      id: nodes.length + 1,
      label: `Node ${nodes.length + 1}`,
      position: { x: Math.random() * 500, y: Math.random() * 500 },
      selected: false,
    };
    setNodes([...nodes, newNode]);
  };

  return (
    <div className="graph-editor" onMouseMove={handleNodeDrag} onMouseUp={handleNodeDragEnd}>
      <div className="button-container">
        <button
          className="add-node-button"
          onClick={addNode}
        >
          Add Node
        </button>
        <button
          className={`add-edge-button ${addingEdge ? 'active' : ''}`}
          onClick={handleAddEdgeClick}
        >
          Add Edge
        </button>
      </div>
      <div className="separator-line"></div>
      {edges.map((edge, index) => (
        <Edge key={index} start={edge.start} end={edge.end} />
      ))}
      {nodes.map((node) => (
        <Node
          key={node.id}
          id={node.id}
          label={node.label}
          position={node.position}
          onDrag={handleNodeDrag}
          onMouseDown={(e) => handleNodeDragStart(e, node.id)}
          onClick={handleNodeClick}
          selected={node.selected}
        />
      ))}
    </div>
  );
};

export default App;
