// src/components/Edge.js
import React from 'react';

const Edge = ({ start, end }) => {
  const x1 = start.x + 50; // offset for node width
  const y1 = start.y + 25; // offset for node height
  const x2 = end.x + 50;   // offset for node width
  const y2 = end.y + 25;   // offset for node height

  return (
    <svg className="edge" width="100%" height="100%">
      <line x1={x1} y1={y1} x2={x2} y2={y2} stroke="black" strokeWidth="2" />
    </svg>
  );
};

export default Edge;
