import React, { useState } from 'react';
import './App.css';

const Node = ({ id, label, position, onDragStart, onDragEnd }) => {
  return (
    <div
      className="node"
      style={{
        left: `${position.x}px`,
        top: `${position.y}px`,
        position: 'absolute',
        backgroundColor: '#4CAF50',
        color: '#fff',
        borderRadius: '10px',
        padding: '10px 20px',
        cursor: 'move',
      }}
      draggable
      onDragStart={(e) => onDragStart(e, id)}
      onDragEnd={(e) => onDragEnd(e, id)}
    >
      {label}
    </div>
  );
};

const Edge = ({ start, end }) => {
  const calculateLine = (start, end) => {
    const x1 = start.x;
    const y1 = start.y;
    const x2 = end.x;
    const y2 = end.y;
    const dx = x2 - x1;
    const dy = y2 - y1;
    const length = Math.sqrt(dx * dx + dy * dy);
    const angle = Math.atan2(dy, dx) * (180 / Math.PI);

    return {
      width: length,
      transform: `rotate(${angle}deg)`,
      left: x1,
      top: y1,
    };
  };

  const lineStyle = calculateLine(start, end);

  return (
    <div
      style={{
        position: 'absolute',
        width: `${lineStyle.width}px`,
        height: '2px',
        backgroundColor: '#000',
        transformOrigin: '0 0',
        transform: lineStyle.transform,
        left: `${lineStyle.left}px`,
        top: `${lineStyle.top}px`,
      }}
    />
  );
};

const App = () => {
  const [nodes, setNodes] = useState([
    { id: 1, label: 'Node 1', position: { x: 100, y: 100 } },
    { id: 2, label: 'Node 2', position: { x: 300, y: 100 } },
  ]);

  const [edges, setEdges] = useState([]);

  const handleNodeDragStart = (e, id) => {
    e.dataTransfer.setData('nodeId', id);
  };

  const handleNodeDragEnd = (e, id) => {
    const newX = e.clientX - 50; // Adjust for node width
    const newY = e.clientY - 25; // Adjust for node height
    setNodes((prevNodes) =>
      prevNodes.map((node) =>
        node.id === id ? { ...node, position: { x: newX, y: newY } } : node
      )
    );
  };

  const addNode = () => {
    const newNode = {
      id: nodes.length + 1,
      label: `Node ${nodes.length + 1}`,
      position: { x: Math.random() * 500, y: Math.random() * 500 },
    };
    setNodes([...nodes, newNode]);
  };

  const addEdge = (startNodeId, endNodeId) => {
    const startNode = nodes.find((node) => node.id === startNodeId);
    const endNode = nodes.find((node) => node.id === endNodeId);

    if (startNode && endNode) {
      setEdges([...edges, { start: startNode.position, end: endNode.position }]);
    }
  };

  return (
    <div className="graph-editor">
      <button onClick={addNode}>Add Node</button>
      <button onClick={() => addEdge(1, 2)}>Add Edge</button>
      <div className="graph-container">
        {edges.map((edge, index) => (
          <Edge key={index} start={edge.start} end={edge.end} />
        ))}
        {nodes.map((node) => (
          <Node
            key={node.id}
            id={node.id}
            label={node.label}
            position={node.position}
            onDragStart={handleNodeDragStart}
            onDragEnd={handleNodeDragEnd}
          />
        ))}
      </div>
    </div>
  );
};

export default App;
